// Módulos y Librerías
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
// Servicio - Modelo
import { GUsuario } from 'src/app/config/Modelos/gusuario';
import { GUsuarioService } from 'src/app/config/Servicios/gusuario.service';

@Component({
  selector: 'app-crear-usuarios',
  templateUrl: './crear-usuarios.component.html',
  styleUrls: ['./crear-usuarios.component.css']
})
export class CrearUsuariosComponent implements OnInit  {
  usuarioForm: FormGroup;
titulo = 'Crear usuario';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              private toastr: ToastrService,
              private _usuarioService:  GUsuarioService,
              private aRouter: ActivatedRoute) { 
    this.usuarioForm = this.fb.group({
      nombre: ['', Validators.required],
      apellidos: ['', Validators.required],
      Fnacimiento: ['', Validators.required],
      email: ['', Validators.required],
      telefono: ['', Validators.required],
      foto: ['', Validators.required],
      pass: ['', Validators.required],
      tipo: ['', Validators.required]
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.esEditar();
  }

  agregarUsuario() {

    const USUARIO: GUsuario = {
      nombre: this.usuarioForm.get('nombre')?.value,
      apellidos: this.usuarioForm.get('apellidos')?.value,
      Fnacimiento: this.usuarioForm.get('Fnacimiento')?.value,
      email: this.usuarioForm.get('email')?.value,
      telefono: this.usuarioForm.get('telefono')?.value,
      foto: this.usuarioForm.get('foto')?.value,
      pass: this.usuarioForm.get('pass')?.value,
      tipo: this.usuarioForm.get('tipo')?.value,
    }

    if(this.id !== null){
      // Editamos Usuario
      this._usuarioService.editarUsuario(this.id, USUARIO).subscribe(data =>{
        this.toastr.info('El usuario fue actualizado con exito!', 'Usuario Actualizado!');
        this.router.navigate(['/']);
      }, error => {
        console.log(error);
        this.usuarioForm.reset();
      })
    } else{
      // Agregamos Usuario
      console.log(USUARIO);
        this._usuarioService.guardarUsuario(USUARIO).subscribe(data => {
        this.toastr.success('El usuario fue registrado con exito!', 'Usuario Registrado!');
        this.router.navigate(['/']);
      }, error => {
        console.log(error);
        this.usuarioForm.reset();
      })
    }
  }

  esEditar() {
    if(this.id !== null) {
      this.titulo = 'Editar usuario';
      this._usuarioService.obtenerUsuario(this.id).subscribe(data => {
        this.usuarioForm.setValue({
          nombre: data.nombre,
          apellido: data.apellidos,
          Fnacimiento: data.Fnacimiento,
          email: data.email,
          telefono: data.telefono,
          foto: data.foto,
          pass: data.pass,
          tipo: data.tipo
        })
      })
    }
  }

}
<div id="todo" class="container mt-15">
    <div class="row">
        <div class="col-lg-12">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">{{ titulo | uppercase }}</span>
                    <form class="mb-3" [formGroup]="usuarioForm" (ngSubmit)="agregarUsuario()">

                        <div class="mb-3">
                            <input type="text" formControlName="nombre" class="form-control" placeholder="nombre">
                            <div class="text-danger" *ngIf="usuarioForm.get('nombre')?.hasError('required') && usuarioForm.get('nombre')?.touched">
                                <span>El nombre es obligatorio</span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <input type="text" formControlName="apellidos" class="form-control" placeholder="apellidos">
                            <div class="text-danger" *ngIf="usuarioForm.get('apellidos')?.hasError('required') && usuarioForm.get('apellidos')?.touched">
                                <span>El apellidos es obligatorio</span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <input type="text" formControlName="Fnacimiento" class="form-control" placeholder="Fnacimiento">
                            <div class="text-danger" *ngIf="usuarioForm.get('Fnacimiento')?.hasError('required') && usuarioForm.get('Fnacimiento')?.touched">
                                <span>La fecha de nacimiento es obligatoria</span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <input type="text" formControlName="email" class="form-control" placeholder="email">
                            <div class="text-danger" *ngIf="usuarioForm.get('email')?.hasError('required') && usuarioForm.get('email')?.touched">
                                <span>El correo electronico es obligatorio</span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <input type="text" formControlName="telefono" class="form-control" placeholder="telefono">
                            <div class="text-danger" *ngIf="usuarioForm.get('telefono')?.hasError('required') && usuarioForm.get('telefono')?.touched">
                                <span>El telefono es obligatorio</span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <input type="text" formControlName="foto" class="form-control" placeholder="foto">
                            <div class="text-danger" *ngIf="usuarioForm.get('foto')?.hasError('required') && usuarioForm.get('foto')?.touched">
                                <span>La foto es obligatoria</span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <input type="text" formControlName="pass" class="form-control" placeholder="pass">
                            <div class="text-danger" *ngIf="usuarioForm.get('pass')?.hasError('required') && usuarioForm.get('pass')?.touched">
                                <span>La contraseña es obligatoria</span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <label for="">Tipo de Usuario</label>
                            <select type="text" formControlName="tipo" lass="form-control" >
                                <option value="Profesor">Profesor</option>
                                <option value="Alumno">Alumno</option>
                            </select>
                        <div class="text-danger" *ngIf="usuarioForm.get('tipo')?.hasError('required') && usuarioForm.get('tipo')?.touched">
                            <span>El tipo es obligatorio</span>
                        </div>
                        </div>

                        <div class="mb-3">
                            <button class="btn btn-secondary float-start" routerLink="/">VOLVER</button>
                            <button type="submit" [disabled]="usuarioForm.invalid" class="btn btn-success float-end">ACEPTAR</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
